import React, { useState, useEffect } from 'react';
import axios from 'axios';
import SalesChart from '../components/SalesChart'; // <-- Import the new chart component

const Dashboard = () => {
    const [totalProducts, setTotalProducts] = useState(0);
    const [totalOrders, setTotalOrders] = useState(0);
    const [totalTurnover, setTotalTurnover] = useState(0);
    // --- STATE FOR CHART DATA ---
    const [salesData, setSalesData] = useState({ labels: [], data: [] });
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const promises = [
            axios.get('/api/products'),
            axios.get('/api/orders')
        ];

        Promise.all(promises)
            .then((responses) => {
                const productsResponse = responses[0];
                const ordersResponse = responses[1];

                setTotalProducts(productsResponse.data.length);
                setTotalOrders(ordersResponse.data.length);

                const turnover = ordersResponse.data.reduce((acc, order) => acc + order.totalPrice, 0);
                setTotalTurnover(turnover);

                // --- PROCESS DATA FOR THE SALES CHART ---
                const salesByDate = {};
                ordersResponse.data.forEach(order => {
                    // Get the date in a simple YYYY-MM-DD format
                    const date = new Date(order.orderDate).toISOString().split('T')[0];
                    // Add the order's total price to the running total for that date
                    salesByDate[date] = (salesByDate[date] || 0) + order.totalPrice;
                });

                // Sort the dates chronologically
                const sortedLabels = Object.keys(salesByDate).sort((a, b) => new Date(a) - new Date(b));
                const sortedData = sortedLabels.map(label => salesByDate[label]);

                setSalesData({
                    labels: sortedLabels,
                    data: sortedData
                });
            })
            .catch(error => {
                console.error('Error fetching dashboard data:', error);
            })
            .finally(() => {
                setLoading(false);
            });
    }, []);

    return (
        <div className="flex flex-col">
            <div className="flex flex-1">
                <main className="flex-1 p-6">
                    <h1 className="text-3xl font-semibold mb-4">Dashboard</h1>

                    {loading ? (
                        <p>Loading dashboard data...</p>
                    ) : (
                        <div>
                            {/* --- METRICS CARDS --- */}
                            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                                <div className="bg-white p-6 rounded shadow-md">
                                    <h2 className="text-xl font-semibold mb-2 text-gray-800">Total Products</h2>
                                    <p className="text-4xl">{totalProducts}</p>
                                </div>
                                <div className="bg-white p-6 rounded shadow-md">
                                    <h2 className="text-xl font-semibold mb-2 text-gray-800">Total Orders</h2>
                                    <p className="text-4xl">{totalOrders}</p>
                                </div>
                                <div className="bg-white p-6 rounded shadow-md">
                                    <h2 className="text-xl font-semibold mb-2 text-gray-800">Total Turnover</h2>
                                    <p className="text-4xl">â‚¹{totalTurnover.toFixed(2)}</p>
                                </div>
                            </div>

                            {/* --- RENDER THE SALES CHART --- */}
                            <div className="grid grid-cols-1 mt-4">
                                <div className="bg-white p-6 rounded shadow-md">
                                    {/* The SalesChart component is now rendered here */}
                                    <SalesChart chartData={salesData} />
                                </div>
                            </div>
                        </div>
                    )}
                </main>
            </div>
        </div>
    );
}

export default Dashboard;
