import React from 'react';

const OrderCard = ({ order, onDelete }) => {
    const handleDelete = () => {
        // FIXED: Use order._id to match MongoDB's key
        onDelete(order._id); 
    };

    return (
        <div className="bg-white p-6 rounded shadow-md">
            {/* FIXED: Displaying the correct '_id' property */}
            <h2 className="text-xl font-semibold mb-2">Order #{order._id}</h2>
            {/* FIXED: Calculating total items from the length of the products array */}
            <p className="text-gray-600">Total Items: {order.products.length}</p>
            <p className="text-gray-600">Total Amount: ${order.totalAmount}</p>
            <button onClick={handleDelete} className="bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded mt-2">
                Delete
            </button>
        </div>
    );
};

export default OrderCard;
