import React from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

// We need to register the components we are using with Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const SalesChart = ({ chartData }) => {
    const options = {
        responsive: true,
        plugins: {
            legend: {
                position: 'top',
            },
            title: {
                display: true,
                text: 'Sales Revenue Over Time',
                font: {
                    size: 18
                }
            },
        },
        scales: {
            y: {
                beginAtZero: true,
                ticks: {
                    // Format the y-axis ticks as currency
                    callback: function(value) {
                        return '₹' + value;
                    }
                }
            }
        }
    };

    const data = {
        labels: chartData.labels, // Dates will go here
        datasets: [
            {
                label: 'Total Revenue',
                data: chartData.data, // Sales amounts will go here
                borderColor: 'rgb(54, 162, 235)',
                backgroundColor: 'rgba(54, 162, 235, 0.5)',
            },
        ],
    };

    return <Line options={options} data={data} />;
};

export default SalesChart;
