import './App.css';
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from 'react-router-dom';

// Component Imports
import LeftNav from './components/LeftNav';
import Footer from './components/Footer';
import Header from './components/Header';

// Page Imports
import Dashboard from './Dashboard';
import InventoryPage from './InventoryPage';
import AddProductForm from '../components/AddProductForm';
import CategoriesPage from './CategoriesPage';
import OrdersPage from './OrdersPage';
import AddOrderPage from './AddOrderPage';
import LandingPage from './LandingPage';
import LoginPage from './LoginPage';

// A helper component to handle navigation logic within the Router context
const AppContent = ({ isAuthenticated, setIsAuthenticated }) => {
    const navigate = useNavigate();

    const handleLogout = () => {
        localStorage.removeItem('userInfo');
        setIsAuthenticated(false);
        navigate('/login');
    };

    return (
        <div>
            {isAuthenticated ? (
                // --- CORRECTED Authenticated User View ---
                <div className='flex min-h-screen bg-gray-100'>
                    <LeftNav />
                    {/* This container now uses flex-col to stack Header, main, and Footer vertically */}
                    <div className='flex flex-col flex-grow lg:w-[85%]'>
                        <Header handleLogout={handleLogout} />
                        {/* The main content area will now grow to fill available space */}
                        <main className="flex-grow p-6">
                            <Routes>
                                <Route path="/dashboard" element={<Dashboard />} />
                                <Route path="/inventory" element={<InventoryPage />} />
                                <Route path="/inventory/add-product" element={<AddProductForm />} />
                                <Route path="/categories" element={<CategoriesPage />} />
                                <Route path="/orders" element={<OrdersPage />} />
                                <Route path="/orders/add" element={<AddOrderPage />} />
                                <Route path="*" element={<Navigate to="/dashboard" />} />
                            </Routes>
                        </main>
                        {/* The Footer is now correctly positioned at the bottom */}
                        <Footer />
                    </div>
                </div>
            ) : (
                // --- Guest User View ---
                <Routes>
                    <Route path="/" element={<LandingPage />} />
                    <Route path="/login" element={<LoginPage setIsAuthenticated={setIsAuthenticated} />} />
                    <Route path="*" element={<Navigate to="/" />} />
                </Routes>
            )}
        </div>
    );
};


const App = () => {
    const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem('userInfo'));

    return (
        <Router>
            <AppContent
                isAuthenticated={isAuthenticated}
                setIsAuthenticated={setIsAuthenticated}
            />
        </Router>
    );
}

export default App;
