import './App.css';
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from 'react-router-dom';

// Component Imports
import LeftNav from './components/LeftNav';
import Footer from './components/Footer';
import Header from './components/Header';

// Page Imports
import Dashboard from './pages/Dashboard';
import InventoryPage from './pages/Inventory';
import AddProductForm from './components/AddProductForm';
import CategoriesPage from './pages/CategoriesPage';
import OrdersPage from './pages/OrdersPage';
import TransactionsPage from './pages/TransactionsPage';
import LandingPage from './pages/LandingPage';
import LoginPage from './pages/LoginPage';

// A helper component to handle navigation logic within the Router context
const AppContent = ({ isAuthenticated, setIsAuthenticated }) => {
    const navigate = useNavigate();

    const handleLogout = () => {
        // 1. Remove user info from localStorage
        localStorage.removeItem('userInfo');
        // 2. Update the authentication state to false
        setIsAuthenticated(false);
        // 3. Redirect the user to the login page
        navigate('/login');
    };

    return (
        <div>
            {isAuthenticated ? (
                // --- Authenticated User View ---
                <div className='flex overflow-x-hidden'>
                    <LeftNav />
                    <div className='lg:w-[85%]'>
                        <div className='h-screen'>
                            {/* Pass the handleLogout function to the Header */}
                            <Header handleLogout={handleLogout} />
                            <main>
                                <Routes>
                                    <Route path="/dashboard" element={<Dashboard />} />
                                    <Route path="/inventory" element={<InventoryPage />} />
                                    <Route path="/inventory/add-product" element={<AddProductForm />} />
                                    <Route path="/categories" element={<CategoriesPage />} />
                                    <Route path="/orders" element={<OrdersPage />} />
                                    <Route path="/transactions" element={<TransactionsPage />} />
                                    {/* Redirect any other path to the dashboard when logged in */}
                                    <Route path="*" element={<Navigate to="/dashboard" />} />
                                </Routes>
                            </main>
                        </div>
                        <Footer />
                    </div>
                </div>
            ) : (
                // --- Guest User View ---
                <Routes>
                    <Route path="/" element={<LandingPage />} />
                    <Route path="/login" element={<LoginPage setIsAuthenticated={setIsAuthenticated} />} />
                    {/* Redirect any other path to the landing page when logged out */}
                    <Route path="*" element={<Navigate to="/" />} />
                </Routes>
            )}
        </div>
    );
};


const App = () => {
    // Initialize isAuthenticated state by checking for 'userInfo' in localStorage.
    // The '!!' converts the result (a string or null) to a true/false boolean.
    const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem('userInfo'));

    return (
        <Router>
            <AppContent
                isAuthenticated={isAuthenticated}
                setIsAuthenticated={setIsAuthenticated}
            />
        </Router>
    );
}

export default App;
